import React, { useContext, useState } from "react";
import './Input.css'

function Input({checked, handleChecked, disabled}) {
    const [value, setValue] = useState('');




    const handleChange = e => {
        setValue(e.target.value)
    }

    const ctx = useContext(value)


  return (
    <>
      <input type="radio" onChange={handleChecked} checked={checked} className="radio" />
      <input type="text" onChange={e => handleChange(e)}  value={value}  disabled={disabled} className="text" />
    </>
  );
}

export default Input;
