import React, { useState } from "react";
import { data } from "../data";
import "./QuestionContainer.css";
import Typist from 'react-typist';
import Question from './Question';

function Questions() {
  const [index, setIndex] = useState(0);
  const [display, setDisplay] = useState(false);
  let flag = true;

  const checkAnswer = (e) => {
    const target = e.target;
    const answer = data[index]
      .map((answer) => answer.correctAnswer)
      .join("");

    if (target.textContent === answer) {
      target.classList.add("correctAnswer");
      if (flag) {
        setDisplay(true);
        flag = false;
      }
    }
  };

  if (data[index]) {
    return (
      <Question index={index} check={checkAnswer} display={display} setIndex={setIndex}/>
    );
  }
  if (index === data.length) {
    return (
      <div className="container">
        <div className="container__end">
            <Typist >
                Brawo, odgadłeś wszystkie flagi! :)
            </Typist>
        </div>
      </div>
    );
  }
}

export default Questions;
