import React, { useState } from "react";
import { data } from "../data";
import "./QuestionContainer.css";
import Question from "./Question";
import Button from "./Button";
import EndScreen from "./EndScreen";

function Questions() {
  const [index, setIndex] = useState(0);
  const [display, setDisplay] = useState(false);
  let flag = true;

  const checkAnswer = (e) => {
    const target = e.target;
    const answer = data[index].map((answer) => answer.correctAnswer).join("");

    if (target.textContent === answer) {
      target.classList.add("correctAnswer");
      if (flag) {
        setDisplay(true);
        flag = false;
      }
    }
  };

  if (data[index]) {
    return (
      <>
        <Question
          index={index}
          check={checkAnswer}
          display={display}
          setIndex={setIndex}
          data={data}
        />
        <Button display={display} setDisplay={setDisplay} index={index} setIndex={setIndex} data={data}/>
      </>
    );
  }
  if (index === data.length) {
    return (
      <EndScreen />
    );
  }
}

export default Questions;
